/*---------------------------------------------------------------------------*/
/*                                                                           */
/*                              User Control Task                            */
/*                                                                           */
/*  This task is used to control your robot during the user control phase of */
/*  a VEX Competition.                                                       */
/*                                                                           */
/*  You must modify the code to add your own robot specific commands here.   */
/*---------------------------------------------------------------------------*/

task usercontrol()
{
	// User control code here, inside the loop

	while (true)
	{
		// This is the main execution loop for the user control program.
		// Each time through the loop your program should update motor + servo
		// values based on feedback from the joysticks.

		// ........................................................................
		// Insert user code here. This is where you use the joystick values to
		// update your motors, etc.
		// ........................................................................

		// Get joystick values
		driverRY = vexRT[Ch1];
		driverRX = vexRT[Ch2];
		driverLY = vexRT[Ch3];
		driverLX = vexRT[Ch4];

		//Get Encoder values
		RDEncoder = SensorValue[RightDriveEnc];
		LDEncoder = SensorValue[LeftDriveEnc];

		//Perform drive math if necessary

		//Tank Drive
		RF = RR = driverRY;
		LF = LR = driverLY;

		//Arcade
		LF = driverLY + driverLX;
		LR = driverLY + driverLX;
		RF = driverLY - driverLX ;
		RR = driverLY - driverLX ;

		//Tank Holonomic
		LF = driverLY + driverLX;
		LR = driverLY - driverLX;
		RF = driverRY + driverRX ;
		RR = driverRY - driverRX ;

		//Halo Holonomic
		LF = driverLY + driverLX + driverRX;
		LR = driverLY - driverLX + driverRX;
		RF = driverLY - driverLX - driverRX;
		RR = driverLY + driverLX - driverRX;

		//Check if output speeds are in acceptable range
		if(RF>127){RF=127;}
		if(RR>127){RR=127;}
		if(LF>127){LF=127;}
		if(LR>127){LR=127;}
		if(RF<-127){RF=-127;}
		if(RR<-127){RR=-127;}
		if(LF<-127){LF=-127;}
		if(LR<-127){LR=-127;}

		//Assign output values to motors
		motor[RightFront] = RF;
		motor[RightRear] = RR;
		motor[LeftFront] = LF;
		motor[LeftRear] = LR;

	}
}
